// GeometricBackground.tsx - 배경 기하학적 패턴 컴포넌트
import React from 'react';

const GeometricBackground: React.FC = () => {
  return (
    <div className="geometricBackground">
      {/* 육각형 모양들 */}
      <div 
        className="geometricShape hexagon1"
        style={{ '--rotate': '30deg' } as React.CSSProperties}
      />
      <div 
        className="geometricShape hexagon2"
        style={{ '--rotate': '-15deg' } as React.CSSProperties}
      />
      <div 
        className="geometricShape hexagon3"
        style={{ '--rotate': '45deg' } as React.CSSProperties}
      />
      <div 
        className="geometricShape hexagon4"
        style={{ '--rotate': '-30deg' } as React.CSSProperties}
      />
      
      {/* 선형 패턴들 */}
      <div className="geometricShape linePattern1" />
      <div className="geometricShape linePattern2" />
    </div>
  );
};

export default GeometricBackground;
